${customClauseSQL}

    and HIS.ID_ = #{taskId,jdbcType=VARCHAR}


    and HIS.TASK_DEF_KEY_ = #{taskDefinitionKey,jdbcType=VARCHAR}


    and HIS.PROC_DEF_ID_ = #{processDefinitionId,jdbcType=VARCHAR}


